// GetPrice returns the price
function getPrice(str) {
     var String=str.substring(str.lastIndexOf("$")+1,str.lastIndexOf("]"));
     var price = parseInt(String)
     return price;
}

// Returns the percent of an option
function getPercent(str) {
     var String=str.substring(str.lastIndexOf("+")+1,str.lastIndexOf("%"));
     var price = parseInt(String)
     return price/100;
}

